import React from "react";

export default function Home() {
  return (
    <div style={{ maxWidth: "800px", margin: "0 auto", fontFamily: "Arial" }}>
      <h1>Welcome to </h1>
      <p>
        This platform allows users to create and manage professional profiles, post and browse jobs,
        and leverage AI-powered features like skill extraction and job-candidate matching.
      </p>
      <p>
        Users can register, log in, edit their profiles, post job openings, and explore recommended jobs
        based on their skills and experience. AI enhancements help provide smart suggestions and
        match scores to make career decisions more efficient.
      </p>
      <p>
        Navigate to the Dashboard to manage your profile and jobs, or click on AI Enhancements to
        see AI-powered tools in action.
      </p>
    </div>
  );
}
