const BASE_URL = "http://127.0.0.1:8000"; // FastAPI backend

// 1️⃣ Extract skills from resume
export async function extractSkills(file) {
  const formData = new FormData();
  formData.append("file", file);

  const res = await fetch(`${BASE_URL}/candidate/extract-skills/`, {
    method: "POST",
    body: formData
  });
  return res.json();
}

// 2️⃣ Get job ↔ applicant match score
export async function matchJob(jobDescription, candidateBio) {
  const res = await fetch(`${BASE_URL}/job/match/`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ job_description: jobDescription, candidate_bio: candidateBio })
  });
  return res.json();
}

// 3️⃣ Get smart job recommendations
export async function getRecommendations(userProfile) {
  const res = await fetch(`${BASE_URL}/recommend/recommend/`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ user_profile: userProfile })
  });
  return res.json();
}
